import React, { useState, useEffect } from "react";
import axios from "axios";
import { Table } from "react-bootstrap";

function Scoreboard() {
  const [gamesPlayed, setGamesPlayed] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:8000/api/scoreboard/")
      .then((response) => {
        setGamesPlayed(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <div>
      <ScoreboardTable games={gamesPlayed} />
    </div>
  );
}

function ScoreboardTable({ games }) {
  return (
    <div>
      <h2>Scoreboard</h2>
      <Table striped bordered hover responsive size="sm">
        <thead>
          <tr>
            <th>Game ID</th>
            <th>Team A</th>
            <th>Score A</th>
            <th>Score B</th>
            <th>Team B</th>
            <th>Game Date</th>
          </tr>
        </thead>
        <tbody>
          {games?.map((game) => (
            <tr key={game.id}>
              <td>{game.id}</td>
              <td>{game.team_a.team_name}</td>

              <td>{game.score_a}</td>
              <td>{game.score_b}</td>
              <td>{game.team_b.team_name}</td>
              <td>
                <p>Team Size: {game.team_b.team_size}</p>
                <p>League Admin: {game.team_b.league_admin}</p>
                <p>Eliminated: {game.team_b.is_eliminated ? "Yes" : "No"}</p>
                <p>Tournament: {game.team_b.tournament}</p>
              </td>
              <td>{game.date}</td>
            </tr>
          ))}
        </tbody>
      </Table>
    </div>
  );
}

export default Scoreboard;
