import React, { useState, useEffect } from "react";
import axios from "axios";
import { Table } from "react-bootstrap";

function Scoreboard() {
  const [gamesPlayed, setGamesPlayed] = useState("");

  useEffect(() => {
    axios
      .get("http://localhost:8000/api/scoreboard/")
      .then((response) => {
        setGamesPlayed(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <div>
      <ScoreboardTable gamesPlayed={gamesPlayed} />
    </div>
  );
}

function ScoreboardTable({ gamesPlayed }) {
  return (
    <div>
      <h2>Scoreboard</h2>
      <Table striped bordered hover responsive size="sm">
        <thead>
          <tr>
            <th>Game ID</th>
            <th>Team A</th>
            <th>Team A Details</th>
            <th>Score A</th>
            <th>Score B</th>
            <th>Team B</th>
            <th>Team B Details</th>
            <th>Game Date</th>
          </tr>
        </thead>
        <tbody>
            <div>
            <tr key={gamesPlayed.id}>
              <td>{gamesPlayed.id}</td>
              <td>{gamesPlayed.team_a.team_name}</td>
              <td>
                <p>Team Size: {gamesPlayed.team_a.team_size}</p>
                <p>League Admin: {gamesPlayed.team_a.league_admin}</p>
                <p>Eliminated: {gamesPlayed.team_a.is_eliminated ? "Yes" : "No"}</p>
                <p>Tournament: {gamesPlayed.team_a.tournament}</p>
              </td>
              <td>{gamesPlayed.score_a}</td>
              <td>{gamesPlayed.score_b}</td>
              <td>{gamesPlayed.team_b.team_name}</td>
              <td>
                <p>Team Size: {gamesPlayed.team_b.team_size}</p>
                <p>League Admin: {gamesPlayed.team_b.league_admin}</p>
                <p>Eliminated: {gamesPlayed.team_b.is_eliminated ? "Yes" : "No"}</p>
                <p>Tournament: {gamesPlayed.team_b.tournament}</p>
              </td>
              <td>{gamesPlayed.date}</td>
            </tr>
            </div>
        </tbody>
      </Table>
    </div>
  );
}

export default Scoreboard;
